import React from "react";

import {
    Card,
    CardBody,
    CardTitle,
    CardText,
    CardSubtitle,
    CardFooter,
    Button,
    Container
}
from "reactstrap";


const Course =({course}) =>
{
    return (
        <div>
          <Card className="text-center">
            <CardBody>
              {/* <CardTitle>Java</CardTitle> */}
              <CardSubtitle style = {{fontWeight:"bolder"}}>{course.title}</CardSubtitle>
              <CardText >{course.description}</CardText>
              <Container className="text-center">

              <Button color="danger">Delete</Button>
              <Button color="warning m-3">Update</Button>

              </Container>
            </CardBody>
          </Card>
        </div>
      );
}
export default Course;
