import React from "react";

import {
    Card,
    CardBody,
    CardTitle,
    CardText,
    CardSubtitle,
    CardFooter,
    Button,
    Container
}
from "reactstrap";


const User =({user}) =>
{

  const deleteUser = ()=>
  {
    alert("delete function call hua");
  }
    return (
        <div>
          <Card className="text-center">
            <CardBody>
              {/* <CardTitle>Java</CardTitle> */}
              <CardSubtitle style = {{fontWeight:"bolder"}}>{user.firstName + " "+user.lastName}</CardSubtitle>
              <CardText >{user.email}</CardText>
              <Container className="text-center">

              <Button color="danger" onClick={deleteUser}>Delete</Button>
              <Button color="warning m-3">Update</Button>

              </Container>
            </CardBody>
          </Card>
        </div>
      );
}
export default User;
