import axios from "axios";
import React, { useEffect,Fragment,useState } from "react";
import { toast } from "react-toastify";
import { Form,FormGroup, Label, Input,Container,Button} from "reactstrap";

const AddUser = ()=>
{
// in case of update id will be passed as parameter
  const { id } = useParams();

  useEffect(()=>
  {
    if(id)
    {
      document.title = "Update User";
    }
    else{
      document.title = "Add User"

    }
  },[])

  const [course,setCourse] = useState({});
  const handleForm = (e)=>
  {
console.log(course);
postDataToServer(course);

e.preventDefault();

  }

  const postDataToServer = (data)=>
  {
    axios.post("http://localhost:8080/users",data).then(
(response)=>
{
  console.log(response);
  // document.getElementById("firstName").value=null;
  // document.getElementById("lastname").value=null;
  // document.getElementById("email").value=null;
  // document.getElementById("userId").value=null;
  console.log("the value of first name is "+document.getElementById("firstName").value );
  console.log("the value of last name is "+document.getElementById("lastName").value );
  console.log("the value of email  is "+document.getElementById("email").value );
  console.log("the value of userI name is "+document.getElementById("userId").value );





  



  toast.success("data saved ssuccessfully");
},
(error)=>
{
  toast.error("error occured while saving data "+ error);
}

    )
  };
    return(

        <Fragment>
<h1 className="text-center my-3">Fill the form</h1>

            <Form onSubmit={handleForm}>

            <FormGroup>
    <Label for="userId">
      userId
    </Label>
    <Input
      id="userId"
      name="userId"
      placeholder="User Id"
      type="number" onChange={

        (e)=>
        {
          setCourse({...course,id:e.target.value});
        }
      }
    />
  </FormGroup>

  <FormGroup>
    <Label for="firstName">
      FirstName
    </Label>
    <Input
      id="firstName"
      name="firstName"
      placeholder="enter firstName"
      type="text"
      onChange={

        (e)=>
        {
          setCourse({...course,firstName:e.target.value});
        }
      }
    />
  </FormGroup>
  <FormGroup>

    <Label for = "lastName">LastName

        <Input type="text" placeholder="Enter lastName here" id="lastName"
        
        onChange={

          (e)=>
          {
            setCourse({...course,lastName:e.target.value});
          }
        }
        style={{width:"55vw"}} />
    </Label>
  </FormGroup>
  <FormGroup>

<Label for = "email">Email

    <Input type="email" placeholder="Enter email here" id="email" 
    
    onChange={

      (e)=>
      {
        setCourse({...course,email:e.target.value});
      }
    }
    style={{width:"55vw"}} ></Input>
</Label>
</FormGroup>

  <Container>
<Button color="success" type="submit">Submit</Button>
<Button color="warning"  type = "reset" style={{marginLeft:"20px"}}>Clear</Button>


  </Container>
            </Form>
        </Fragment>
    );
}
export default AddUser;
